import {useParams} from "react-router-dom";

export function Single(){

    const {id} = useParams();
    // throw new Error("Page inexistante !");
    return (
        <div className="mx-32 my-20">
            <h1>Article {id}</h1>
            <div className="grid grid-cols-1 md:grid-cols-3 md:gap-10">
                {formations.map(formation => 

                <div className="max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700" key={formation.id}>
                    {console.log(formation.img)}
                    <a href="/">
                        <img className="rounded-t-lg" src={formation.img} alt="Hello" />
                    </a>
                    <div className="p-5">
                        <a href="/">
                            <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{formation.nom}</h5>
                        </a>
                        <p className="mb-3 font-normal text-gray-700 dark:text-gray-400 text-truncate">{formation.descriptif}</p>
                        <div className="flex justify-evenly">
                            <p>{formation.duree} mois</p> 
                            <p>Secteur : {formation.zone}</p>
                        </div>
                        <a href="/" className="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
                            En lire plus
                            <svg className="w-3.5 h-3.5 ml-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
                                <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M1 5h12m0 0L9 1m4 4L9 9"/>
                            </svg>
                        </a>
                    </div>
                </div>

                )}
            </div>
        </div>
    )
}
